import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { useGlobalContext } from '../context/global';

function WatchList() {
  const { watchlist } = useGlobalContext();

  return (
    <WatchListStyled>

<header>
                <div className="logo">
                    <h1>
                        {rendered === 'popular' ? 'Popular Anime' : 
                        rendered === 'ongoing' ? 'Ongoing Anime' : 'Upcoming Anime'}
                    </h1>
                </div>
                <div className="search-container">
                    <div className="filter-btn popular-filter">
                        <button onClick={() => {
                            setRendered('popular')
                        }}>Popular<i className="fas fa-fire"></i></button>
                    </div>
                   
                    <div className="filter-btn airing-filter">
                        <button onClick={() => {
                            setRendered('ongoing')
                            getAiringAnime()
                        }}>Ongiong<i class="fa-solid fa-star"></i></button>
                    </div>
                    <div className="filter-btn upcoming-filter">
                        <button onClick={() => {
                            setRendered('upcoming')
                            getUpcomingAnime()
                        }}>Upcoming<i class="fa-solid fa-timer"></i></button>
                    </div>

                    <form action="" className="search-form" onSubmit={handleSubmit}>
                        <div className="input-control">
                            <input type="text" placeholder="Search Anime" value={search} onChange={handleChange} />
                            <button type="submit">Search</button>
                        </div>
                    </form>

                    <Link to="Components/WatchList" className="filter-btn watchlist-filter">
                     <button>Watchlist<i className="fas fa-list"></i></button>
                     </Link>

                </div>
            </header>        
      <h1>Мой список ожидания</h1>
      <div className="anime-list">
        {watchlist.length > 0 ? (
          watchlist.map((anime) => (
            <Link to={`/anime/${anime.mal_id}`} key={anime.mal_id}>
              <div className="anime">
                <img src={anime.images.jpg.large_image_url} alt="" />
                <h4>{anime.title}</h4>
              </div>
            </Link>
          ))
        ) : (
          <p>Список ожидания пуст</p>
        )}
      </div>
    </WatchListStyled>
  );
}

const WatchListStyled = styled.div`
  padding: 3rem 18rem;

  h1 {
    font-size: 3rem;
    margin-bottom: 1.5rem;
    text-align: center;
  }

  .anime-list {
    display: grid;
    grid-template-columns: repeat(auto-fill, minmax(300px, 1fr));
    grid-gap: 2rem;

    .anime {
      display: flex;
      flex-direction: column;
      align-items: center;
      gap: 1rem;

      img {
        width: 100%;
        border-radius: 5px;
      }

      h4 {
        font-size: 1.2rem;
        text-align: center;
      }
    }
  }
`;

export default WatchList;
